function J = jetk(m)%jetk :	Variant of HSV.%	jetk(M), a variant of JET(M). Goes black->blue->red->black%	jetk, by itself, is the same length as the current colormap.%	Use colormap(jetk).if nargin < 1   m = size(get(gcf,'colormap'),1);endx = [[ 0  0  1  2 -1];     [ 0  1  2  -1 0];     [ 1  2 -1  0  0];];J = zeros(m,3);for i = 1:3    for j=1:5        switch x(i,j)            case -1                J((floor((j-1)/5*m)+1):ceil(j/5*m),i) = linspace(1,0,ceil(j/5*m)-floor((j-1)/5*m));            case 0                J((floor((j-1)/5*m)+1):ceil(j/5*m),i) = zeros(ceil(j/5*m)-floor((j-1)/5*m),1);            case 1                J((floor((j-1)/5*m)+1):ceil(j/5*m),i) = linspace(0,1,ceil(j/5*m)-floor((j-1)/5*m));            case 2                J((floor((j-1)/5*m)+1):ceil(j/5*m),i) = ones(ceil(j/5*m)-floor((j-1)/5*m),1);        end    endend                